import React, { useState } from 'react'
import './App.css';
import * as ReactDOM from 'react-dom';

function App() {
  let localStorageData = () => {
    let data = JSON.parse(localStorage.getItem("Items"));
    return data || [];
  }

  const [getStickyText, setStickyText] = useState("");
  const [getTime, setTime] = useState(0);
  const [getItemList, setItemList] = useState(localStorageData());

  const dataText = (e) => {
    setStickyText(e.target.value)
  }
  const dataHr = (e) => {
    setTime(e.target.value);
  }
  let main = document.querySelector('#main')
  const addStickyNotes = () => {

    if (getStickyText === "" && getTime === 0) {
      alert("How do I create a blank space? 😒 Populate it before proceeding!")
      return;

      setItemList(() => {
        let subList = [...getItemList, {
          Item_text: getStickyText,
          Item_time: getTime
        }]
        localStorage.setItem('Items', JSON.stringify(subList));
        return subList;
      })
      setStickyText("");
      setTime(0);
    }


  }

  //todo ------- increase hours -------
  function incHour(idx) {
    let data = [...subjectList];
    data[idx].hourInput = parseInt(data[idx].hourInput) + 1;
    setSubjectList(data);
    localStorage.setItem('subjects', JSON.stringify(data));
  }
  //todo ------- decrease hours -------
  function dscHour(idx) {
    let data = [...getItemList];
    if (data[idx].It <= 1) {
      alert("Please study atleast 1 hour for better future 😉")
    }
    else {
      data[idx].hourInput = parseInt(data[idx].hourInput) - 1;
    }
    setSubjectList(data);
    localStorage.setItem('subjects', JSON.stringify(data))
  }
  //todo ------- Delete subject -------
  function delSubject(idx) {
    subjectList.splice(idx, 1);
    setSubjectList([...subjectList]);
    localStorage.setItem('subjects', JSON.stringify([...subjectList]));
  }

  return (
    <div className="App">
      <h1>Sticky Notes</h1>
      <div className="btn">
        <input type="text" placeholder="Subject" onChange={(e) => dataText(e)} ></input>
        <input type="number" placeholder="Hour" onChange={(e) => dataHr(e)}></input>
        <button onClick={addStickyNotes}>Add</button>
      </div>
      <div id="main" >
        <p
          style={{ display: getItemList.length == 0 ? 'block' : 'none' }}>No planner added yet</p>
        {
          getItemList.map((subject, idx) => {

            return (
              <div className="subContainer">
                <p>{subject.Item_text}</p>
                <p>{subject.Item_time} Hours</p>
                <div className="btns">
                  <button
                    onClick={(e) => incHour(idx)}
                    style={{ backgroundColor: "#65B741" }}
                    title="Increase Study Time"
                  >➕</button>
                  <button
                    onClick={(e) => dscHour(idx)}
                    style={{ backgroundColor: "#F24C3D" }}
                    title="Decrease Study Time"
                  >➖</button>
                  <button
                    style={{ backgroundColor: "#FFACAC" }}
                    title="Delete The Planner"
                    onClick={(e) =>
                      delSubject(idx)
                    }
                  >❌</button>
                </div>
              </div>
            )
          })
        }
      </div>

    </div>
  );
}

export default App;
