import React from 'react'

const main = (props) => {
  console.log(props.jsonData);
  return (
    <div>
      {
        props.jsonData.map((data, idx) => {
          return
          (<div className="subContainer">
            <p>{data.subjectInput}</p>
            <p>{data.hourInput} Hours</p>
            <div className="btns">
              <button
                onClick={(e) => incHour(idx)}
                style={{ backgroundColor: "#65B741" }}
                title="Increase Study Time"
              >➕</button>
              <button
                onClick={(e) => dscHour(idx)}
                style={{ backgroundColor: "#F24C3D" }}
                title="Decrease Study Time"
              >➖</button>
              <button
                style={{ backgroundColor: "#FFACAC" }}
                title="Delete The Planner"
                onClick={(e) =>
                  delSubject(idx)
                }
              >❌</button>
            </div>
          </div>)
        })
      }
    </div>
  )
}

export default main
