import React,{useState,useEffect} from 'react'
import './App.css';



function App() {
  let [desc,setDesc]=useState("hello world");
  let random="";
  let counter=0;
  useEffect(() =>{

    setInterval( ()=> {
        for (let index = 0; index <10; index++) {
            random+=JSON.stringify(Math.round(Math.random()));
          }
          counter++;   
          setDesc(random)
         } , 1000);

         if(counter==10){
          clearInterval();
         }

},[])




  return (
    <div className="App">
    {desc}
    </div>
  );
}

export default App;
