import React,{useState,useEffect} from 'react'
import './App.css';



function App() {
  let [desc,setDesc]=useState("hello world");
  let random="";
  let [counter,setCounter]=uState(0);

  useEffect(() =>{

    let time=setInterval( ()=> {
        for (let index = 0; index <10; index++) {
            random+=JSON.stringify(Math.round(Math.random()));
          }
          counter=counter+1;   
          setDesc(random)
          random="";
          if(counter==10){
            clearInterval(time);
            setDesc("Hello World!");
           }
         } , 1000);



},[])




  return (
    <div className="App">
    {desc}
    </div>
  );
}

export default App;
